import React, { useEffect, useState } from "react";
import {
  Box,
  Typography,
  Card,
  useTheme,
  useMediaQuery,
} from "@mui/material";
import ChatHeader from "./ChatHeader";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import ChatLog from "./chatLogs";
import BottomBar from "./bottomBar";
import ChatGroupDetails from "./Models/Chatgroupdetails";

const Chat = ({ currentChat, socket, Messages, setMessages }) => {
  const theme = useTheme();
  const [viewGroup, setViewGroup] = useState(false);
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    if (!socket) return;

    const handleMessage = (message) => {
      setMessages((prev) => [...prev, message]);
    };

    const handleConnection = () => {
      setIsConnected(true);
    };

    const handleDisconnection = () => {
      setIsConnected(false);
    };

    socket.on("newMessage", handleMessage);
    socket.on("connect", handleConnection);
    socket.on("disconnect", handleDisconnection);

    // Verify message delivery on reload
    // if (Messages.length > 0) {
    //   Messages.forEach((msg) => {
    //     socket.emit("messageDelivered", msg.id); // Assuming each message has a unique id
    //   });
    // }

    return () => {
      socket.off("newMessage", handleMessage);
      socket.off("connect", handleConnection);
      socket.off("disconnect", handleDisconnection);
    };
  }, [socket, setMessages, Messages]);

  const sendMessage = (messageContent) => {
    if (socket && isConnected) {
      const message = {
        content: messageContent,
        chatId: currentChat.id,
        senderId: socket.id, // Assuming the socket ID is the sender's ID
        timestamp: new Date().toISOString(),
      };
      socket.emit("sendMessage", message);
      setMessages((prev) => [...prev, message]); // Optimistically update the UI
    }
  };

  return (
    <Box
      sx={{
        width: "100%",
        height: "76vh",
        display: "flex",
        flexDirection: "column",
      }}
    >
          {viewGroup ? (
        <ChatGroupDetails 
          currentChat={currentChat} 
          setViewGroup={setViewGroup} 
        />
      ) : (
      
        currentChat ? (
        <Card
          sx={{
            height: "99%",
            width: "100%",
            display: "flex",
            flexDirection: "column",
            boxShadow: "none",
            
            borderRadius: "20px",  // Curved edges for the main card
            overflow: "hidden",
          }}
        >
          {/* Chat Header */}
          <ChatHeader 
              currentChat={currentChat} 
              onViewGroup={setViewGroup} 
            />

          {/* Message Log */}
          <Box
            sx={{
              flex: 1,
              overflowY: "auto",
              display: "flex",
              flexDirection: "column",
              gap: 10,
            }}
          >

            {/* <Box
              sx={{
                display: "flex",
                justifyContent: "center",
                marginBottom: "0px",
              }}
            >
              <Box
                sx={{
                  backgroundColor: "#FEECDC",
                  display: "flex",
                  alignItems: "center",
                  gap: "8px",
                  borderRadius: "12px",
                  padding: "8px 16px",
                  maxWidth: "480px",
                  textAlign: "center",
                }}
              >
                <LockOutlinedIcon sx={{ color: "#312E40" }} />
                <Typography
                  sx={{
                    fontSize: "12px",
                    color: "#312E40",
                    fontWeight: 400,
                  }}
                >
                  Messages are end-to-end encrypted. No one outside of this
                  chat, not even WhatsApp, can read or listen to them.
                </Typography>
              </Box>
            </Box>*/}
            <ChatLog
  socket={socket}
  Messages={Messages}
  currentUserId={currentChat.currentUserId} // Replace with the actual user ID
  setMessages={setMessages}
/>
          </Box>

          {/* Bottom Bar */}
          <Box
            sx={{
              padding: "8px",
              borderTop: "1px solid #ddd",
              backgroundColor: "#FFFFFF", // Bottom bar background color
            }}
          >
            <BottomBar socket={socket} community={currentChat} sendMessage={sendMessage} />
          </Box>
        </Card>
      ) : (
        <Box
          sx={{
            flex: 1,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            textAlign: "center",
            ml: "10px",
          }}
        >
          <Box
            sx={{
              display: "flex",
              flexDirection: "column", // Stack image and text vertically
              alignItems: "center", // Keep the image and text centered
            }}
          >
            <img
              src="data:image/png;base64, "
              alt="No Chat Selected"
            />
            <Typography
      sx={{
        color:"blue",
        fontSize: "14px",
        fontWeight: 500,
        lineHeight: "24px",
        ml: "20px", // Add margin only for the text
      }}
    >
      Send and receive messages here...
    </Typography>
  </Box>
  </Box>
      )
)}
  </Box>

  );
};

export default Chat;